    private void InDocumentoMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_InDocumentoMousePressed
        if (InDocumento.getText().equals("Ingrese su documento")) {
            InDocumento.setText("");
            InDocumento.setForeground(Color.black);
        }
        if (String.valueOf(InUsuario.getText()).isEmpty()) {
            InUsuario.setText("Ingrese su nombre de usuario");
            InUsuario.setForeground(new Color(204,204,204));
        }
    }//GEN-LAST:event_InDocumentoMousePressed

