    private void InUsuarioMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_InUsuarioMousePressed
        if (String.valueOf(InDocumento.getText()).isEmpty()) {
            InDocumento.setText("Ingrese su documento");
            InDocumento.setForeground(new Color(204,204,204));
        }
        if (InUsuario.getText().equals("Ingrese su nombre de usuario")) {
        InUsuario.setText("");
        InUsuario.setForeground(Color.black);
        }
    }//GEN-LAST:event_InUsuarioMousePressed

    private void EntrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_EntrarMouseClicked
        javax.swing.JOptionPane.showMessageDialog(this, "Ingreso de login con los datos:\nDocumento: " + InDocumento.getText() + "\nUsuario: " + InUsuario.getText(), "LOGIN", javax.swing.JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_EntrarMouseClicked

